mapper.select:
SELECT a.id, a.type, a.title, a.thumbnail, a.subtitle, a.summary, a.label, a.examine_user_id AS examineUserId, b.name AS examineUserName, a.create_user_id AS createUserId, c.name AS createUserName, a.status, a.publish_time AS publishTime, a.publish_target AS publishTarget FROM publicity_message a LEFT JOIN org_user b ON a.examine_user_id = b.id LEFT JOIN org_user c ON a.create_user_id = c.id WHERE a.is_delete = 0
AND a.status IN
#{item}
AND a.status = #{status}
AND a.title LIKE CONCAT('%',#{title},'%')
AND a.type = #{type}
AND a.create_user_id = #{createUserId}
AND c.name LIKE CONCAT('%',#{createUserName},'%')
ORDER BY a.create_time DESC

SELECT a.id, a.title, a.thumbnail, a.subtitle, a.summary, a.label, a.attachments, a.publish_time AS publishTime FROM publicity_message a LEFT JOIN org_user b ON a.examine_user_id = b.id LEFT JOIN org_user c ON a.create_user_id = c.id WHERE a.`status` = 9 AND a.is_delete = 0 AND a.type = 2
AND a.publish_time >= #{publishTime}
AND a.id != #{id}
ORDER BY publish_time ASC,id ASC LIMIT 1

SELECT a.id, a.title, a.thumbnail, a.subtitle, a.summary, a.label, a.attachments, a.publish_time AS publishTime FROM publicity_message a LEFT JOIN org_user b ON a.examine_user_id = b.id LEFT JOIN org_user c ON a.create_user_id = c.id WHERE a.`status` = 9 AND a.is_delete = 0 AND a.type = 2
AND a.publish_time <= #{publishTime}
AND a.id != #{id}
ORDER BY publish_time DESC,id DESC LIMIT 1
